import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
          await import("@replit/vite-plugin-dev-banner").then((m) =>
            m.devBanner(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { z } from "zod";
import { 
  InstituteType, 
  Department, 
  JobLevel, 
  EmploymentType,
  JobSearchFilters 
} from "@shared/schema";
import { validateFirebaseToken } from "./services/firebase-admin";
import { sendEmail } from "./services/email";
import { storage } from "./storage";

// User interface is now extended globally via types/express.d.ts

// API Response helper
const createApiResponse = (data?: any, error?: string, message?: string) => ({
  success: !error,
  data,
  error,
  message,
});

// Middleware to validate Firebase token
const authenticateUser = async (req: Request, res: Response, next: NextFunction) => {
  try {
    const token = req.headers.authorization?.replace('Bearer ', '');
    if (!token) {
      return res.status(401).json(createApiResponse(null, 'No token provided'));
    }

    const decodedToken = await validateFirebaseToken(token);
    req.user = decodedToken as any; // Compatible with optional fields
    next();
  } catch (error) {
    res.status(401).json(createApiResponse(null, 'Invalid token'));
  }
};

// Middleware to check email verification
const requireEmailVerification = (req: Request, res: Response, next: NextFunction) => {
  if (!req.user?.email_verified) {
    return res.status(403).json(createApiResponse(null, 'Email verification required'));
  }
  next();
};

// Middleware to check user role
const requireRole = (roles: string[]) => (req: Request, res: Response, next: NextFunction) => {
  const userRole = req.user?.role || 'seeker';
  if (!roles.includes(userRole)) {
    return res.status(403).json(createApiResponse(null, 'Insufficient permissions'));
  }
  next();
};

// Validation schemas
const companySchema = z.object({
  name: z.string().min(2),
  website: z.string().url().optional(),
  instituteType: InstituteType,
  hrEmail: z.string().email(),
  phone: z.string().optional(),
  address: z.string().min(10),
});

const jobSchema = z.object({
  title: z.string().min(5),
  department: Department,
  level: JobLevel,
  instituteType: InstituteType,
  employmentType: EmploymentType,
  location: z.object({
    city: z.string(),
    state: z.string(),
    country: z.string().default('India'),
  }),
  minSalary: z.number().optional(),
  maxSalary: z.number().optional(),
  currency: z.string().default('INR'),
  qualifications: z.array(z.string()).default([]),
  skills: z.array(z.string()).default([]),
  responsibilities: z.array(z.string()).default([]),
  description: z.string().min(50),
  requirements: z.string().optional(),
  lastDate: z.string().transform(str => new Date(str)),
  companyId: z.string(),
});

const applicationSchema = z.object({
  jobId: z.string(),
  resumePath: z.string(),
  coverLetter: z.string().optional(),
});

// Type assertion helper to ensure user is authenticated
function assertUser(req: Request): asserts req is Request & { user: Express.UserPayload } {
  if (!req.user) {
    throw new Error('User not authenticated');
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Health check
  app.get('/api/health', (req, res) => {
    res.json(createApiResponse({ status: 'OK', timestamp: new Date().toISOString() }));
  });

  // Auth routes
  app.post('/api/auth/send-verification', authenticateUser, async (req, res) => {
    try {
      const { email } = req.user;

      // Send verification email
      const emailSent = await sendEmail({
        to: email,
        subject: 'Verify your EduHire Faculty account',
        html: `
          <h1>Welcome to EduHire Faculty!</h1>
          <p>Please verify your email address by clicking the link below:</p>
          <p>This is a demo - email verification would be handled by Firebase Auth.</p>
        `
      });

      if (emailSent) {
        res.json(createApiResponse(null, null, 'Verification email sent'));
      } else {
        res.status(500).json(createApiResponse(null, 'Failed to send email'));
      }
    } catch (error) {
      res.status(500).json(createApiResponse(null, 'Internal server error'));
    }
  });

  // Company routes
  app.post('/api/companies', authenticateUser, requireEmailVerification, async (req, res) => {
    try {
      const validatedData = companySchema.parse(req.body);

      const company = await storage.createCompany({
        ...validatedData,
        ownerUid: req.user.uid,
        proofDocs: [],
      });

      res.json(createApiResponse(company));
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json(createApiResponse(null, 'Validation error', error.errors[0].message));
      } else {
        res.status(500).json(createApiResponse(null, 'Failed to create company'));
      }
    }
  });

  app.get('/api/companies/me', authenticateUser, requireEmailVerification, async (req, res) => {
    try {
      const company = await storage.getCompanyByOwner(req.user.uid);
      res.json(createApiResponse(company));
    } catch (error) {
      res.status(500).json(createApiResponse(null, 'Failed to get company'));
    }
  });

  app.put('/api/companies/:id', authenticateUser, requireEmailVerification, async (req, res) => {
    try {
      const companyId = req.params.id;
      const validatedData = companySchema.partial().parse(req.body);

      // Check if user owns the company
      const company = await storage.getCompany(companyId);
      if (!company || company.ownerUid !== req.user.uid) {
        return res.status(403).json(createApiResponse(null, 'Not authorized'));
      }

      // Don't allow updates if company is approved (except specific fields)
      if (company.status === 'approved') {
        const allowedFields = ['phone', 'address'];
        const updateData = Object.keys(validatedData).reduce((acc: any, key) => {
          if (allowedFields.includes(key)) {
            acc[key] = validatedData[key as keyof typeof validatedData];
          }
          return acc;
        }, {});

        await storage.updateCompany(companyId, updateData);
      } else {
        await storage.updateCompany(companyId, validatedData);
      }

      res.json(createApiResponse(null, null, 'Company updated successfully'));
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json(createApiResponse(null, 'Validation error', error.errors[0].message));
      } else {
        res.status(500).json(createApiResponse(null, 'Failed to update company'));
      }
    }
  });

  // Admin route to get pending companies
  app.get('/api/admin/companies/pending', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['admin']), 
    async (req, res) => {
      try {
        const companies = await storage.getPendingCompanies();
        res.json(createApiResponse(companies));
      } catch (error) {
        res.status(500).json(createApiResponse(null, 'Failed to get pending companies'));
      }
    }
  );

  // Admin route to approve/reject companies
  app.patch('/api/admin/companies/:id/status', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['admin']), 
    async (req, res) => {
      try {
        const companyId = req.params.id;
        const { status } = req.body;

        if (!['approved', 'rejected'].includes(status)) {
          return res.status(400).json(createApiResponse(null, 'Invalid status'));
        }

        await storage.updateCompany(companyId, { status });

        // Log audit entry
        await storage.createAuditLog({
          actorUid: req.user.uid,
          action: status === 'approved' ? 'company_approved' : 'company_rejected',
          targetType: 'company',
          targetId: companyId,
          metadata: { status },
        });

        res.json(createApiResponse(null, null, `Company ${status} successfully`));
      } catch (error) {
        res.status(500).json(createApiResponse(null, `Failed to ${req.body.status} company`));
      }
    }
  );

  // Job routes
  app.post('/api/jobs', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['employer']), 
    async (req, res) => {
      try {
        const validatedData = jobSchema.parse(req.body);

        // Check if company is approved
        const company = await storage.getCompany(validatedData.companyId);
        if (!company || company.status !== 'approved' || company.ownerUid !== req.user.uid) {
          return res.status(403).json(createApiResponse(null, 'Company must be approved to post jobs'));
        }

        const job = await storage.createJob({
          ...validatedData,
          posterUid: req.user.uid,
          status: 'pending',
        });

        res.json(createApiResponse(job));
      } catch (error) {
        if (error instanceof z.ZodError) {
          res.status(400).json(createApiResponse(null, 'Validation error', error.errors[0].message));
        } else {
          res.status(500).json(createApiResponse(null, 'Failed to create job'));
        }
      }
    }
  );

  app.get('/api/jobs', async (req, res) => {
    try {
      const page = parseInt(req.query.page as string) || 1;
      const limit = Math.min(parseInt(req.query.limit as string) || 20, 100);
      const filters = {
        department: req.query.department as string,
        instituteType: req.query.instituteType as string,
        level: req.query.level as string,
        location: req.query.location as string,
        employmentType: req.query.employmentType as string,
        sortBy: req.query.sortBy as string || 'newest',
        query: req.query.q as string,
      };

      const result = await storage.searchJobs(filters, page, limit);
      res.json(createApiResponse(result));
    } catch (error) {
      res.status(500).json(createApiResponse(null, 'Failed to search jobs'));
    }
  });

  app.get('/api/jobs/featured', async (req, res) => {
    try {
      const limit = Math.min(parseInt(req.query.limit as string) || 6, 20);
      const jobs = await storage.getFeaturedJobs(limit);
      res.json(createApiResponse(jobs));
    } catch (error) {
      res.status(500).json(createApiResponse(null, 'Failed to get featured jobs'));
    }
  });

  app.get('/api/jobs/:id', async (req, res) => {
    try {
      const job = await storage.getJob(req.params.id);
      if (!job) {
        return res.status(404).json(createApiResponse(null, 'Job not found'));
      }

      // Increment view count
      await storage.updateJob(job.id, { 
        viewCount: (job.viewCount || 0) + 1 
      });

      res.json(createApiResponse(job));
    } catch (error) {
      res.status(500).json(createApiResponse(null, 'Failed to get job'));
    }
  });

  // Admin routes for jobs
  app.get('/api/admin/jobs/pending', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['admin']), 
    async (req, res) => {
      try {
        const jobs = await storage.getPendingJobs();
        res.json(createApiResponse(jobs));
      } catch (error) {
        res.status(500).json(createApiResponse(null, 'Failed to get pending jobs'));
      }
    }
  );

  app.patch('/api/admin/jobs/:id/status', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['admin']), 
    async (req, res) => {
      try {
        const jobId = req.params.id;
        const { status } = req.body;

        if (!['approved', 'rejected'].includes(status)) {
          return res.status(400).json(createApiResponse(null, 'Invalid status'));
        }

        const updateData: any = { status };
        if (status === 'approved') {
          updateData.approvedBy = req.user.uid;
          updateData.approvedAt = new Date();
        }

        await storage.updateJob(jobId, updateData);

        // Log audit entry
        await storage.createAuditLog({
          actorUid: req.user.uid,
          action: status === 'approved' ? 'job_approved' : 'job_rejected',
          targetType: 'job',
          targetId: jobId,
          metadata: { status },
        });

        res.json(createApiResponse(null, null, `Job ${status} successfully`));
      } catch (error) {
        res.status(500).json(createApiResponse(null, `Failed to ${req.body.status} job`));
      }
    }
  );

  // Application routes
  app.post('/api/applications', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['seeker']), 
    async (req, res) => {
      try {
        const validatedData = applicationSchema.parse(req.body);

        // Check if job is approved
        const job = await storage.getJob(validatedData.jobId);
        if (!job || job.status !== 'approved') {
          return res.status(400).json(createApiResponse(null, 'Job not available for applications'));
        }

        // Check if application deadline has passed
        if (job.lastDate < new Date()) {
          return res.status(400).json(createApiResponse(null, 'Application deadline has passed'));
        }

        // Check for duplicate application
        const dedupeKey = `${validatedData.jobId}_${req.user.uid}`;
        const existingApplication = await storage.getApplicationByDedupeKey(dedupeKey);
        if (existingApplication) {
          return res.status(400).json(createApiResponse(null, 'You have already applied to this job'));
        }

        const application = await storage.createApplication({
          ...validatedData,
          applicantUid: req.user.uid,
          dedupeKey,
        });

        // Increment application count
        await storage.updateJob(job.id, { 
          applicationCount: (job.applicationCount || 0) + 1 
        });

        res.json(createApiResponse(application));
      } catch (error) {
        if (error instanceof z.ZodError) {
          res.status(400).json(createApiResponse(null, 'Validation error', error.errors[0].message));
        } else {
          res.status(500).json(createApiResponse(null, 'Failed to submit application'));
        }
      }
    }
  );

  app.get('/api/applications/me', 
    authenticateUser, 
    requireEmailVerification, 
    requireRole(['seeker']), 
    async (req, res) => {
      try {
        const applications = await storage.getApplicationsByUser(req.user.uid);
        res.json(createApiResponse(applications));
      } catch (error) {
        res.status(500).json(createApiResponse(null, 'Failed to get applications'));
      }
    }
  );

  // Stats route
  app.get('/api/stats', async (req, res) => {
    try {
      const stats = await storage.getStats();
      res.json(createApiResponse(stats));
    } catch (error) {
      res.status(500).json(createApiResponse(null, 'Failed to get stats'));
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  User, 
  Company, 
  Job, 
  Application, 
  Report, 
  AuditLog,
  InsertUser, 
  InsertCompany, 
  InsertJob, 
  InsertApplication, 
  InsertReport, 
  InsertAuditLog,
  JobSearchFilters,
  PaginatedResponse,
  UserRole,
  CompanyStatus,
  JobStatus,
  ApplicationStatus
} from "@shared/schema";
import { randomUUID } from "crypto";

// Enhanced storage interface for faculty jobs marketplace
export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<void>;
  deleteUser(id: string): Promise<void>;

  // Company operations
  getCompany(id: string): Promise<Company | undefined>;
  getCompanyByOwner(ownerUid: string): Promise<Company | undefined>;
  createCompany(company: InsertCompany): Promise<Company>;
  updateCompany(id: string, updates: Partial<Company>): Promise<void>;
  getPendingCompanies(): Promise<Company[]>;
  getApprovedCompanies(): Promise<Company[]>;

  // Job operations
  getJob(id: string): Promise<Job | undefined>;
  createJob(job: InsertJob): Promise<Job>;
  updateJob(id: string, updates: Partial<Job>): Promise<void>;
  deleteJob(id: string): Promise<void>;
  getJobsByCompany(companyId: string): Promise<Job[]>;
  getJobsByPoster(posterUid: string): Promise<Job[]>;
  getPendingJobs(): Promise<Job[]>;
  getApprovedJobs(): Promise<Job[]>;
  getFeaturedJobs(limit?: number): Promise<Job[]>;
  searchJobs(filters: Partial<JobSearchFilters>, page?: number, limit?: number): Promise<PaginatedResponse<Job>>;

  // Application operations
  getApplication(id: string): Promise<Application | undefined>;
  getApplicationByDedupeKey(dedupeKey: string): Promise<Application | undefined>;
  createApplication(application: InsertApplication): Promise<Application>;
  updateApplication(id: string, updates: Partial<Application>): Promise<void>;
  getApplicationsByUser(applicantUid: string): Promise<Application[]>;
  getApplicationsByJob(jobId: string): Promise<Application[]>;

  // Report operations
  getReport(id: string): Promise<Report | undefined>;
  createReport(report: InsertReport): Promise<Report>;
  updateReport(id: string, updates: Partial<Report>): Promise<void>;
  getPendingReports(): Promise<Report[]>;

  // Audit log operations
  createAuditLog(log: InsertAuditLog): Promise<AuditLog>;
  getAuditLogs(limit?: number): Promise<AuditLog[]>;

  // Statistics
  getStats(): Promise<{
    totalJobs: number;
    totalApplications: number;
    totalEmployers: number;
    totalSeekers: number;
    pendingJobs: number;
    pendingEmployers: number;
    activeJobs: number;
  }>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private companies: Map<string, Company> = new Map();
  private jobs: Map<string, Job> = new Map();
  private applications: Map<string, Application> = new Map();
  private reports: Map<string, Report> = new Map();
  private auditLogs: Map<string, AuditLog> = new Map();

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = {
      ...insertUser,
      id,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<void> {
    const user = this.users.get(id);
    if (user) {
      this.users.set(id, { ...user, ...updates, updatedAt: new Date() });
    }
  }

  async deleteUser(id: string): Promise<void> {
    this.users.delete(id);
    // Also clean up related data
    const userCompanies = Array.from(this.companies.values()).filter(c => c.ownerUid === id);
    userCompanies.forEach(company => this.companies.delete(company.id));

    const userJobs = Array.from(this.jobs.values()).filter(j => j.posterUid === id);
    userJobs.forEach(job => this.jobs.delete(job.id));

    const userApplications = Array.from(this.applications.values()).filter(a => a.applicantUid === id);
    userApplications.forEach(app => this.applications.delete(app.id));
  }

  // Company operations
  async getCompany(id: string): Promise<Company | undefined> {
    return this.companies.get(id);
  }

  async getCompanyByOwner(ownerUid: string): Promise<Company | undefined> {
    return Array.from(this.companies.values()).find(company => company.ownerUid === ownerUid);
  }

  async createCompany(insertCompany: InsertCompany): Promise<Company> {
    const id = randomUUID();
    const company: Company = {
      ...insertCompany,
      id,
      status: 'pending',
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.companies.set(id, company);
    return company;
  }

  async updateCompany(id: string, updates: Partial<Company>): Promise<void> {
    const company = this.companies.get(id);
    if (company) {
      this.companies.set(id, { ...company, ...updates, updatedAt: new Date() });
    }
  }

  async getPendingCompanies(): Promise<Company[]> {
    return Array.from(this.companies.values())
      .filter(company => company.status === 'pending')
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getApprovedCompanies(): Promise<Company[]> {
    return Array.from(this.companies.values())
      .filter(company => company.status === 'approved')
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  // Job operations
  async getJob(id: string): Promise<Job | undefined> {
    return this.jobs.get(id);
  }

  async createJob(insertJob: InsertJob): Promise<Job> {
    const id = randomUUID();
    const job: Job = {
      ...insertJob,
      id,
      status: 'pending',
      viewCount: 0,
      applicationCount: 0,
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.jobs.set(id, job);
    return job;
  }

  async updateJob(id: string, updates: Partial<Job>): Promise<void> {
    const job = this.jobs.get(id);
    if (job) {
      this.jobs.set(id, { ...job, ...updates, updatedAt: new Date() });
    }
  }

  async deleteJob(id: string): Promise<void> {
    this.jobs.delete(id);
    // Clean up related applications
    const jobApplications = Array.from(this.applications.values()).filter(a => a.jobId === id);
    jobApplications.forEach(app => this.applications.delete(app.id));
  }

  async getJobsByCompany(companyId: string): Promise<Job[]> {
    return Array.from(this.jobs.values())
      .filter(job => job.companyId === companyId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getJobsByPoster(posterUid: string): Promise<Job[]> {
    return Array.from(this.jobs.values())
      .filter(job => job.posterUid === posterUid)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getPendingJobs(): Promise<Job[]> {
    return Array.from(this.jobs.values())
      .filter(job => job.status === 'pending')
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getApprovedJobs(): Promise<Job[]> {
    return Array.from(this.jobs.values())
      .filter(job => job.status === 'approved')
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getFeaturedJobs(limit: number = 6): Promise<Job[]> {
    return Array.from(this.jobs.values())
      .filter(job => job.status === 'approved')
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(0, limit);
  }

  async searchJobs(filters: Partial<JobSearchFilters>, page: number = 1, limit: number = 20): Promise<PaginatedResponse<Job>> {
    let allJobs = Array.from(this.jobs.values()).filter(job => job.status === 'approved');

    // Apply filters
    if (filters.department) {
      allJobs = allJobs.filter(job => job.department === filters.department);
    }
    if (filters.instituteType) {
      allJobs = allJobs.filter(job => job.instituteType === filters.instituteType);
    }
    if (filters.level) {
      allJobs = allJobs.filter(job => job.level === filters.level);
    }
    if (filters.location) {
      allJobs = allJobs.filter(job => 
        job.location.city.toLowerCase().includes(filters.location!.toLowerCase()) ||
        job.location.state.toLowerCase().includes(filters.location!.toLowerCase())
      );
    }
    if (filters.employmentType) {
      allJobs = allJobs.filter(job => job.employmentType === filters.employmentType);
    }

    // Text search
    if (filters.query) {
      const query = filters.query.toLowerCase();
      allJobs = allJobs.filter(job =>
        job.title.toLowerCase().includes(query) ||
        job.description.toLowerCase().includes(query) ||
        job.qualifications.some(q => q.toLowerCase().includes(query)) ||
        job.skills.some(s => s.toLowerCase().includes(query))
      );
    }

    // Date filtering
    if (filters.postedWithin && filters.postedWithin !== 'all') {
      const now = new Date();
      let daysAgo = 0;
      switch (filters.postedWithin) {
        case '24h': daysAgo = 1; break;
        case '7d': daysAgo = 7; break;
        case '30d': daysAgo = 30; break;
      }
      const dateThreshold = new Date(now.getTime() - (daysAgo * 24 * 60 * 60 * 1000));
      allJobs = allJobs.filter(job => job.createdAt >= dateThreshold);
    }

    // Sorting
    switch (filters.sortBy) {
      case 'deadline':
        allJobs.sort((a, b) => a.lastDate.getTime() - b.lastDate.getTime());
        break;
      case 'salary_high':
        allJobs.sort((a, b) => (b.maxSalary || 0) - (a.maxSalary || 0));
        break;
      case 'salary_low':
        allJobs.sort((a, b) => (a.minSalary || 0) - (b.minSalary || 0));
        break;
      default: // 'newest'
        allJobs.sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
        break;
    }

    // Pagination
    const total = allJobs.length;
    const startIndex = (page - 1) * limit;
    const endIndex = startIndex + limit;
    const items = allJobs.slice(startIndex, endIndex);
    const hasMore = endIndex < total;

    return {
      items,
      total,
      page,
      limit,
      hasMore,
    };
  }

  // Application operations
  async getApplication(id: string): Promise<Application | undefined> {
    return this.applications.get(id);
  }

  async getApplicationByDedupeKey(dedupeKey: string): Promise<Application | undefined> {
    return Array.from(this.applications.values()).find(app => app.dedupeKey === dedupeKey);
  }

  async createApplication(insertApplication: InsertApplication): Promise<Application> {
    const id = randomUUID();
    const application: Application = {
      ...insertApplication,
      id,
      status: 'submitted',
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.applications.set(id, application);
    return application;
  }

  async updateApplication(id: string, updates: Partial<Application>): Promise<void> {
    const application = this.applications.get(id);
    if (application) {
      this.applications.set(id, { ...application, ...updates, updatedAt: new Date() });
    }
  }

  async getApplicationsByUser(applicantUid: string): Promise<Application[]> {
    return Array.from(this.applications.values())
      .filter(application => application.applicantUid === applicantUid)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getApplicationsByJob(jobId: string): Promise<Application[]> {
    return Array.from(this.applications.values())
      .filter(application => application.jobId === jobId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  // Report operations
  async getReport(id: string): Promise<Report | undefined> {
    return this.reports.get(id);
  }

  async createReport(insertReport: InsertReport): Promise<Report> {
    const id = randomUUID();
    const report: Report = {
      ...insertReport,
      id,
      status: 'pending',
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.reports.set(id, report);
    return report;
  }

  async updateReport(id: string, updates: Partial<Report>): Promise<void> {
    const report = this.reports.get(id);
    if (report) {
      this.reports.set(id, { ...report, ...updates, updatedAt: new Date() });
    }
  }

  async getPendingReports(): Promise<Report[]> {
    return Array.from(this.reports.values())
      .filter(report => report.status === 'pending')
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  // Audit log operations
  async createAuditLog(insertLog: InsertAuditLog): Promise<AuditLog> {
    const id = randomUUID();
    const log: AuditLog = {
      ...insertLog,
      id,
      timestamp: new Date(),
    };
    this.auditLogs.set(id, log);
    return log;
  }

  async getAuditLogs(limit: number = 50): Promise<AuditLog[]> {
    return Array.from(this.auditLogs.values())
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime())
      .slice(0, limit);
  }

  // Statistics
  async getStats(): Promise<{
    totalJobs: number;
    totalApplications: number;
    totalEmployers: number;
    totalSeekers: number;
    pendingJobs: number;
    pendingEmployers: number;
    activeJobs: number;
  }> {
    const totalJobs = this.jobs.size;
    const totalApplications = this.applications.size;
    const totalEmployers = Array.from(this.users.values()).filter(u => u.role === 'employer').length;
    const totalSeekers = Array.from(this.users.values()).filter(u => u.role === 'seeker').length;
    const pendingJobs = Array.from(this.jobs.values()).filter(j => j.status === 'pending').length;
    const pendingEmployers = Array.from(this.companies.values()).filter(c => c.status === 'pending').length;
    const activeJobs = Array.from(this.jobs.values()).filter(j => j.status === 'approved').length;

    return {
      totalJobs,
      totalApplications,
      totalEmployers,
      totalSeekers,
      pendingJobs,
      pendingEmployers,
      activeJobs,
    };
  }
}

export const storage = new MemStorage();

