  location: z.object({
    city: z.string(),
    state: z.string(),
    country: z.string().default('India'),
  }),
  minSalary: z.number().optional(),
  maxSalary: z.number().optional(),
  currency: z.string().default('INR'),
  qualifications: z.array(z.string()),
  skills: z.array(z.string()),
  responsibilities: z.array(z.string()),
  description: z.string().min(50),
  requirements: z.string().optional(),
  lastDate: z.any().transform(val => admin.firestore.Timestamp.fromDate(new Date(val))),
  companyId: z.string(),
});

// Helper function to send emails
async function sendEmail(to: string, subject: string, html: string): Promise<boolean> {
  if (!process.env.SENDGRID_API_KEY) {
    console.warn('SendGrid API key not configured');
    return false;
  }

  try {
    await sgMail.send({
      to,
      from: process.env.FROM_EMAIL || 'noreply@eduhire.faculty.com',
      subject,
      html,
    });
    return true;
  } catch (error) {
    console.error('Email sending error:', error);
    return false;
  }
}

// Helper function to create audit log
async function createAuditLog(
  actorUid: string,
  action: string,
  targetType: string,
  targetId: string,
  metadata?: any
): Promise<void> {
  await db.collection('auditLogs').add({
    actorUid,
    action,
    targetType,
    targetId,
    metadata: metadata || {},
    timestamp: admin.firestore.FieldValue.serverTimestamp(),
  });
}

// 1. User creation trigger - Create user profile on authentication
export const onAuthUserCreate = functions
  .region(region)
  .auth.user()
